import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/Dashboard2.scss";

const Dashboard = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [patientDetails, setPatientDetails] = useState({
    name: "",
    id: "",
    department: "Internal Medicine",
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [voiceNote, setVoiceNote] = useState(false);
  const [additionalNotes, setAdditionalNotes] = useState("");
  const navigate = useNavigate();

  const handlePatientDetailsChange = (e) => {
    const { name, value } = e.target;
    setPatientDetails((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileUpload = (event) => {
    const files = Array.from(event.target.files);
    const updatedFiles = files.map((file) => ({
      name: file.name,
      status: "uploading",
    }));
    setUploadedFiles((prevFiles) => [...prevFiles, ...updatedFiles]);

    setTimeout(() => {
      setUploadedFiles((prevFiles) =>
        prevFiles.map((file) =>
          updatedFiles.some((f) => f.name === file.name)
            ? { ...file, status: file.name.endsWith(".pdf") ? "success" : "failed" }
            : file
        )
      );
    }, 2000);
  };

  const handleRetry = (fileName) => {
    setUploadedFiles((prevFiles) =>
      prevFiles.map((file) =>
        file.name === fileName ? { ...file, status: "uploading" } : file
      )
    );

    setTimeout(() => {
      setUploadedFiles((prevFiles) =>
        prevFiles.map((file) =>
          file.name === fileName
            ? { ...file, status: file.name.endsWith(".pdf") ? "success" : "failed" }
            : file
        )
      );
    }, 2000);
  };

  useEffect(() => {
    const validateStep = (step) => {
      if (step === 0) return patientDetails.name.trim() && patientDetails.id.trim();
      if (step === 1) return uploadedFiles.some((file) => file.status === "success");
      if (step === 2) return voiceNote;
      if (step === 3) return additionalNotes.trim();
      return false;
    };

    if (validateStep(currentStep)) {
      setTimeout(() => {
        if (currentStep < 3) setCurrentStep((prev) => prev + 1);
      }, 500);
    }
  }, [patientDetails, uploadedFiles, voiceNote, additionalNotes, currentStep]);

  const handleGenerateSummary = () => navigate("/summary");

  return (
    <div className="dashboard-page">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-logo">AutoDoc</div>
        <nav className="sidebar-links">
          <a href="/dashboard" className="nav-link active">Dashboard</a>
          <a href="/history" className="nav-link">History</a>
        </nav>
        <div className="bottom-links">
          <a href="/settings" className="nav-link">
            <img src="/assets/settings.png" alt="Settings" className="icon" /> Settings
          </a>
          <a href="/logout" className="nav-link">
            <img src="/assets/logout.png" alt="Logout" className="icon" /> Logout
          </a>
        </div>
      </aside>

      {/* Main Content */}
      <div className="main-content">
        {/* Progress Steps */}
        <div className="progress-steps">
          {["Patient Details", "Upload Documents", "Voice Notes", "Additional Notes"].map((step, index) => (
            <div key={index} className={`step ${index <= currentStep ? "completed" : ""}`}>
              <div className="circle">{index + 1}</div>
              <p>{step}</p>
            </div>
          ))}
        </div>

        {/* Step Cards */}
        <div className="step-cards">
          {/* Step 1 */}
          <div className="card">
            <h3>Patient Details (Step 1)</h3>
            <input type="text" placeholder="Patient Name" name="name" value={patientDetails.name} onChange={handlePatientDetailsChange} />
            <input type="text" placeholder="Patient ID" name="id" value={patientDetails.id} onChange={handlePatientDetailsChange} />
            <select name="department" value={patientDetails.department} onChange={handlePatientDetailsChange}>
              <option value="Internal Medicine">Internal Medicine</option>
              <option value="Pediatrics">Pediatrics</option>
            </select>
          </div>

          {/* Step 2 */}
          <div className="card">
            <h3>Upload Documents (Step 2)</h3>
            <input type="file" multiple onChange={handleFileUpload} />
            <ul>
              {uploadedFiles.map((file, index) => (
                <li key={index}>
                  {file.name} - {file.status}
                  {file.status === "failed" && <button onClick={() => handleRetry(file.name)}>Retry</button>}
                </li>
              ))}
            </ul>
          </div>

          {/* Step 3 */}
          <div className="card">
            <h3>Voice Notes (Step 3)</h3>
            <button onClick={() => setVoiceNote(true)}>Record</button>
          </div>

          {/* Step 4 */}
          <div className="card">
            <h3>Additional Notes (Step 4)</h3>
            <textarea value={additionalNotes} onChange={(e) => setAdditionalNotes(e.target.value)} />
          </div>
        </div>

        <button onClick={handleGenerateSummary}>Generate Summary</button>
      </div>
    </div>
  );
};

export default Dashboard;
