import React, { useState } from 'react';
import '../styles/OTPPage.scss';

const OTPPage = () => {
  // State to store each digit of the OTP
  const [otp, setOtp] = useState(Array(6).fill(''));

  const handleChange = (value, index) => {
    // Only allow numeric input or empty
    if (!/^\d*$/.test(value)) return;

    const newOtp = [...otp];
    newOtp[index] = value.slice(-1); // Only keep the last typed digit
    setOtp(newOtp);

    // Automatically move focus to next input
    if (value && index < 5) {
      const nextInput = document.getElementById(`otp-input-${index + 1}`);
      nextInput.focus();
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`OTP Submitted: ${otp.join('')}`);
  };

  return (
    <div className="otp-page">
      <div className="otp-card">
        <h2>Enter verification code</h2>
        <p>Enter the OTP received on +918295641110</p>

        <form onSubmit={handleSubmit} className="otp-form">
          <div className="otp-inputs">
            {otp.map((digit, i) => (
              <input
                key={i}
                id={`otp-input-${i}`}
                type="text"
                maxLength="1"
                value={digit}
                onChange={(e) => handleChange(e.target.value, i)}
              />
            ))}
          </div>

          <button type="submit" className="otp-submit">
            Submit
          </button>
        </form>

        <div className="otp-resend">
          Resend OTP (30)
        </div>
      </div>
    </div>
  );
};

export default OTPPage;
