import React, { useState } from "react";
import "../styles/Dashboard2.scss";

const Dashboard = () => {
  const steps = [
    { title: "Patient Details", description: "Enter basic patient information", completed: true },
    { title: "Upload Documents", description: "Add relevant medical reports", completed: true },
    { title: "Voice Notes", description: "Record your observations", completed: false },
    { title: "Additional Notes", description: "Add any other important details", completed: false },
  ];

  const [uploadedFiles, setUploadedFiles] = useState([
    { name: "blood_report.pdf", status: "success" },
    { name: "xray_scan.jpg", status: "failed" },
    { name: "mri_report.pdf", status: "uploading" },
  ]);

  const handleRetry = (fileName) => {
    console.log(`Retrying upload for: ${fileName}`);
  };

  return (
    <div className="dashboard-page">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-logo">AutoDoc</div>
        <nav className="sidebar-links">
          <a href="/dashboard" className="nav-link active">Dashboard</a>
          <a href="/patient" className="nav-link">Patient</a>
          <a href="/chat" className="nav-link">Chat</a>
        </nav>
        <div className="bottom-links">
          <a href="/settings" className="nav-link">
            <img src="/assets/settings.png" alt="Settings" className="icon" />
            Settings
          </a>
          <a href="/logout" className="nav-link">
            <img src="/assets/logout.png" alt="Logout" className="icon" />
            Logout
          </a>
        </div>
      </aside>

      {/* Main Content */}
      <div className="main-content">
        {/* Progress Steps */}
        <div className="progress-steps">
          {steps.map((step, index) => (
            <div key={index} className={`step ${step.completed ? "completed" : ""}`}>
              <div className="circle">{step.completed ? "âœ”" : index + 1}</div>
              <div className="step-info">
                <h4>{step.title}</h4>
                <p>{step.description}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Patient Form */}
        <div className="form-section">
          <input type="text" placeholder="Patient Name" className="input" />
          <input type="text" placeholder="Patient ID" className="input" />
          <select className="select">
            <option>Internal Medicine</option>
            <option>Cardiology</option>
            <option>Neurology</option>
          </select>
          <button className="generate-button">Generate Summary</button>
        </div>

        {/* Documents Section */}
        <div className="documents-section">
          <h3>Documents (Step 2)</h3>
          <div className="upload-area">
            <p>Drag and drop files here or</p>
            <button className="browse-button">Browse Files</button>
          </div>
          <ul className="uploaded-files">
            {uploadedFiles.map((file, index) => (
              <li key={index} className={`file-item ${file.status}`}>
                {file.name}
                {file.status === "failed" && (
                  <button
                    className="retry-button"
                    onClick={() => handleRetry(file.name)}
                  >
                    Retry
                  </button>
                )}
              </li>
            ))}
          </ul>
        </div>

        {/* Voice Notes Section */}
        <div className="voice-notes-section">
          <h3>Voice Notes (Step 3)</h3>
          <button className="record-button">ðŸ”´ Recording...</button>
          <div className="transcription">Start speaking to see transcription...</div>
        </div>

        {/* Additional Notes Section */}
        <div className="additional-notes-section">
          <h3>Additional Notes (Step 4)</h3>
          <textarea
            className="additional-notes-textarea"
            placeholder="Enter any additional notes here..."
          ></textarea>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
