import React, { useState } from "react";
import "../styles/signup.scss";

const LoginSignup = () => {
  const [phoneNumber, setPhoneNumber] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle OTP request logic here
  };

  const handleInputChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const isButtonDisabled = phoneNumber.length < 10;

  return (
    <div className="login-signup-container">
      <div className="login-signup-card">
        <h2>Login or Sign Up</h2>
        <p>We will send a verification code on this number</p>
        <form onSubmit={handleSubmit}>
          <label htmlFor="phone-number">Enter Phone Number</label>
          <div className="phone-input">
            <span className="country-code">+91</span>
            <input
              type="tel"
              id="phone-number"
              name="phone-number"
              placeholder="Enter your phone number"
              required
              value={phoneNumber}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit" className="get-otp-button" disabled={isButtonDisabled}>
            Get OTP
          </button>
        </form>
        <p className="privacy-policy">
          By continuing you agree to our <a href="/privacy-policy">Privacy Policy</a>
        </p>
      </div>
    </div>
  );
};

export default LoginSignup;
