import React, { useState, useEffect } from "react";
import "../styles/Dashboard2.scss";

const Dashboard = () => {
  const [currentStep, setCurrentStep] = useState(0); // Tracks the current completed step (0-indexed)
  const [patientDetails, setPatientDetails] = useState({
    name: "",
    id: "",
    department: "Internal Medicine",
  });
  const [uploadedFiles, setUploadedFiles] = useState([
    { name: "blood_report.pdf", status: "success" },
    { name: "xray_scan.jpg", status: "failed" },
    { name: "mri_report.pdf", status: "uploading" },
  ]);
  const [voiceNote, setVoiceNote] = useState(false); // Simulates a voice note being recorded
  const [additionalNotes, setAdditionalNotes] = useState("");

  const handlePatientDetailsChange = (e) => {
    const { name, value } = e.target;
    setPatientDetails((prev) => ({ ...prev, [name]: value }));
  };

  const handleRetry = (fileName) => {
    console.log(`Retrying upload for: ${fileName}`);
  };

  const handleFileUpload = (file) => {
    if (file.name.endsWith(".pdf")) {
      setUploadedFiles((prevFiles) => [
        ...prevFiles,
        { name: file.name, status: "success" },
      ]);
    } else {
      alert("Only PDF files are allowed!");
    }
  };

  // Validation logic for each step
  useEffect(() => {
    const validateStep = (step) => {
      if (step === 0) {
        // Step 1: Patient Details
        return patientDetails.name.trim() && patientDetails.id.trim();
      } else if (step === 1) {
        // Step 2: Upload Documents
        return uploadedFiles.some(
          (file) => file.status === "success" && file.name.endsWith(".pdf")
        );
      } else if (step === 2) {
        // Step 3: Voice Notes
        return voiceNote; // Simulates a completed voice note
      } else if (step === 3) {
        // Step 4: Additional Notes
        return additionalNotes.trim();
      }
      return false;
    };

    // Check if the current step is valid
    if (validateStep(currentStep)) {
      setTimeout(() => {
        if (currentStep < 3) {
          setCurrentStep((prev) => prev + 1); // Automatically advance to the next step
        } else {
          alert("All steps completed!");
        }
      }, 500); // Small delay for user feedback
    }
  }, [patientDetails, uploadedFiles, voiceNote, additionalNotes, currentStep]);

  return (
    <div className="dashboard-page">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-logo">Auto Doc</div>
        <nav className="sidebar-links">
          <a href="/dashboard" className="nav-link active">Dashboard</a>
          <a href="/patient" className="nav-link">Patient</a>
          <a href="/chat" className="nav-link">Chat</a>
        </nav>
        <div className="bottom-links">
          <a href="/settings" className="nav-link">
            <img src="/assets/settings.png" alt="Settings" className="icon" />
            Settings
          </a>
          <a href="/logout" className="nav-link">
            <img src="/assets/logout.png" alt="Logout" className="icon" />
            Logout
          </a>
        </div>
      </aside>

      {/* Main Content */}
      <div className="main-content">
        {/* Progress Steps */}
        <div className="progress-steps">
          {["Patient Details", "Upload Documents", "Voice Notes", "Additional Notes"].map((step, index) => (
            <div
              key={index}
              className={`step ${index <= currentStep ? "completed" : ""}`}
            >
              <div className="circle">{index + 1}</div>
              <p>{step}</p>
            </div>
          ))}
        </div>

        {/* Step Cards */}
        <div className="step-cards">
          {/* Step 1: Patient Details */}
          <div className="card">
            <h3>Patient Details (Step 1)</h3>
            <div className="patient-details-form">
              <div className="form-group">
                <label>Patient Name</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter patient name"
                  value={patientDetails.name}
                  onChange={handlePatientDetailsChange}
                />
              </div>
              <div className="form-group">
                <label>Patient ID</label>
                <input
                  type="text"
                  name="id"
                  placeholder="Enter patient ID"
                  value={patientDetails.id}
                  onChange={handlePatientDetailsChange}
                />
              </div>
              <div className="form-group">
                <label>Department</label>
                <select
                  name="department"
                  value={patientDetails.department}
                  onChange={handlePatientDetailsChange}
                >
                  <option value="Internal Medicine">Internal Medicine</option>
                  <option value="Pediatrics">Pediatrics</option>
                  <option value="Cardiology">Cardiology</option>
                  <option value="Neurology">Neurology</option>
                </select>
              </div>
            </div>
          </div>

          {/* Step 2: Documents */}
          <div className="card">
            <h3>Documents (Step 2)</h3>
            <div className="upload-area">
              <p>Drag and drop files here or</p>
              <button
                className="browse-button"
                onClick={() =>
                  handleFileUpload({ name: "example_document.pdf" }) // Simulating a file upload
                }
              >
                Browse Files
              </button>
            </div>
            <ul className="uploaded-files">
              {uploadedFiles.map((file, index) => (
                <li key={index} className={`file-item ${file.status}`}>
                  {file.name}
                  {file.status === "failed" && (
                    <button
                      className="retry-button"
                      onClick={() => handleRetry(file.name)}
                    >
                      Retry
                    </button>
                  )}
                </li>
              ))}
            </ul>
          </div>

          {/* Step 3: Voice Notes */}
          <div className="card">
            <h3>Voice Notes (Step 3)</h3>
            <button
              className="record-button"
              onClick={() => setVoiceNote(true)}
            >
              🔴 Start Recording
            </button>
            <div className="transcription">Start speaking to see transcription...</div>
          </div>

          {/* Step 4: Additional Notes */}
          <div className="card">
            <h3>Additional Notes (Step 4)</h3>
            <textarea
              className="additional-notes-textarea"
              placeholder="Enter any additional notes here..."
              value={additionalNotes}
              onChange={(e) => setAdditionalNotes(e.target.value)}
            ></textarea>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
